import axiosClient from './axiosClient';

const MessengerAPI = {
	getMessage: () => {
		const url = `/auth/mess/${query}`;
		return axiosClient.get(url);
	},

	postMessage: (query) => {
		const url = `/messenger/send${query}`;
		return axiosClient.post(url);
	},
};

export default MessengerAPI;
