import { BrowserRouter, Route, Switch } from "react-router-dom";
import Chat from "./Chat/Chat";
import Header from "./Header/Header";
import History from "./History/History";
import Home from "./Home/Home";
import Menu from "./Menu/Menu";
import Products from "./Products/Products";
import Users from "./Users/Users";
import NewProduct from "./New/NewProduct";
import Update from "./update/update";
import { useEffect } from "react";
import Login from "./Login/Login";
import jwt_decode from "jwt-decode";
import Cookies from "universal-cookie";
import ProtectedRoute from "./ProtectedRoute";

function App() {
  const cookies = new Cookies();

  useEffect(() => {
    const token = cookies.get("accessToken");
    
    const decodedToken = token ? jwt_decode(token) : {};
    const { role } = decodedToken;
    console.log(decodedToken);
    console.log(role)

    if (role !== "admin" && window.location.pathname !== "/login") {
      window.location.href = "/login";
    }
  }, [cookies]);

  return (
    <div className="App">
      <BrowserRouter>
        <div
          id="main-wrapper"
          data-theme="light"
          data-layout="vertical"
          data-navbarbg="skin6"
          data-sidebartype="full"
          data-sidebar-position="fixed"
          data-header-position="fixed"
          data-boxed-layout="full"
        >
          <Header />
          <Menu />
          <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/chat" component={Chat} />
            <Route path="/login" component={Login} />
            <ProtectedRoute path="/users" component={Users} isAdmin={true} />
            <ProtectedRoute
              exact
              path="/products"
              component={Products}
              isAdmin={true}
            />
            <ProtectedRoute path="/new" component={NewProduct} isAdmin={true} />
            <ProtectedRoute path="/history" component={History} isAdmin={true} />
            <ProtectedRoute
              path="/products/view-edit/:id"
              component={Update}
              isAdmin={true}
            />
          </Switch>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
