import React, { useState } from "react";
import { Link, Redirect } from "react-router-dom";
import "./Login.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [checkPush, setCheckPush] = useState(false);
  const [errorLogin, setErrorLogin] = useState(false);
  const [data, setData] = useState([]);

  const onChangeEmail = (e) => {
    setEmail(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const onSubmit = async () => {
    try {
      console.log(email, password);
      const response = await fetch("http://localhost:5001/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
        credentials: "include", // Enable resource sharing across domains
      });

      const responseData = await response.json();
      console.log(responseData);

      if (response.ok && responseData.accessToken) {
        localStorage.setItem("id_user", responseData._id);
        localStorage.setItem("name_user", responseData.fullName);
        document.cookie = `accessToken=${responseData.accessToken}; path=/`; // Store access token as a cookie
        setCheckPush(true);
      } else {
        setErrorLogin(true);
      }
    } catch (error) {
      console.log(error);
    }
  };

  if (checkPush) {
    return <Redirect to="/" />;
  }

  return (
    <div className="page-wrapper">
      <div className="page-breadcrumb">
        <div className="row">
          <div className="login">
            <div className="heading">
              <h2>Sign in</h2>
              <form>
                <div className="input-group input-group-lg">
                  <span className="input-group-addon">
                    <i className="fa fa-user"></i>
                  </span>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Email"
                    value={email}
                    onChange={onChangeEmail}
                  />
                </div>

                <div className="input-group input-group-lg">
                  <span className="input-group-addon">
                    <i className="fa fa-lock"></i>
                  </span>
                  <input
                    type="password"
                    className="form-control"
                    placeholder="Password"
                    value={password}
                    onChange={onChangePassword}
                  />
                </div>

                <button type="button" className="float" onClick={onSubmit}>
                  Login
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
