import React, { useState, useEffect } from "react";
import ProductAPI from "../API/ProductAPI";
import { Link, useParams } from "react-router-dom";

const Update = () => {
  const [productName, setProductName] = useState("");
  const [category, setCategory] = useState("");
  const [shortDescription, setShortDescription] = useState("");
  const [longDescription, setLongDescription] = useState("");
  const [price, setPrice] = useState("");
  const { id } = useParams();

  useEffect(async () => {
    try {
      const response = await ProductAPI.getDetail(id);
      const productData = response.data;
      console.log(productData);
      setProductName(productData.name);
      setCategory(productData.category);
      setShortDescription(productData.short_desc);
      setLongDescription(productData.long_desc);
      setPrice(productData.price);
    } catch (error) {
      console.error(error);
    }
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const formData = new FormData();
      formData.append("id", id);
      formData.append("name", productName);
      formData.append("category", category);
      formData.append("short_desc", shortDescription);
      formData.append("long_desc", longDescription);
      formData.append("price", price);

      const response = await ProductAPI.update(formData);
      console.log(response);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="page-wrapper">
      <div className="page-breadcrumb">
        <div className="row">
          <form
            style={{ width: "50%", marginLeft: "40px" }}
            onSubmit={handleSubmit}
            encType="multipart/form-data"
          >
            <div className="form-group">
              <label>Product Name</label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter Product Name"
                name="productName"
                value={productName} onChange={e => setProductName()}
              />
            </div>
            <div className="form-group">
              <label>Category</label>
              <input
                type="text"
                className="form-control"
                placeholder="Enter Category"
                name="category"
                value={category}
              />
            </div>
            <div className="form-group">
              <label>Short Description</label>
              <textarea
                className="form-control"
                rows="3"
                placeholder="Enter Short Description"
                name="shortDescription"
                value={shortDescription}
              ></textarea>
            </div>
            <div className="form-group">
              <label>Long Description</label>
              <textarea
                className="form-control"
                rows="6"
                placeholder="Enter Long Description"
                name="longDescription"
                value={longDescription}
              ></textarea>
            </div>
            <div className="form-group">
              <label>Price</label>
              <input
                type="number"
                className="form-control"
                placeholder="Enter Price"
                name="price"
                value={price}
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Update
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Update;
