import axiosClient from './axiosClient';

const UserAPI = {
	postSignin: () => {
		const url = `/auth/login/${query}`;
		return axiosClient.post(url);
	},

	getDetailData: (id) => {
		const url = `/auth/${id}`;
		return axiosClient.get(url);
	},

	postSignUp: (query) => {
		const url = `/auth/signup/${query}`;
		return axiosClient.post(url);
	},
};

export default UserAPI;
