import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import jwt_decode from "jwt-decode"; // Sử dụng thư viện để giải mã JWT Token
import Cookies from "universal-cookie";

function Name(props) {
  const [userName, setUserName] = useState(""); // State để lưu tên người dùng
  const cookies = new Cookies();

  useEffect(() => {
    // Lấy JWT Token từ Local Storage
    const token = cookies.get("accesToken"); 
    console.log(token);
    setUserName(token)

    if (token) {
      // Giải mã JWT Token để lấy thông tin người dùng
      const decodedToken = jwt_decode(token);

      // Truy cập thông tin người dùng, trong ví dụ này, ta lấy tên người dùng
      const { name } = decodedToken;

      // Cập nhật state để hiển thị tên người dùng
      setUserName(name);
    }
  }, []);

  return (
    <li className="nav-item dropdown">
      <a
        className="nav-link dropdown-toggle"
        style={{ cursor: "pointer" }}
        id="pagesDropdown"
        data-toggle="dropdown"
        aria-haspopup="true"
        aria-expanded="false"
      >
        <i className="fas fa-user-alt mr-1 text-gray"></i>
        {userName}
      </a>
      <div className="dropdown-menu mt-3" aria-labelledby="pagesDropdown">
        <Link
          className="dropdown-item border-0 transition-link"
          to={"/history"}
        >
          History
        </Link>
      </div>
    </li>
  );
}

export default Name;
