import axiosClient from './axiosClient';

const UserAPI = {
  postSignin: (body) => {
    const url = '/auth/login';
    return axiosClient.post(url, { email, password });
  },

  postSignUp: (query) => {
    const url = '/auth/register';
    return axiosClient.post(url, query);
  },
};

export default UserAPI;
